mapper.select:
select report.id , report.article_id , article.title , report.content , report.gmt_create
from bbs_report as report join bbs_article as article
on report.article_id = article.id
where report.gmt_create > #{beginTime} and report.gmt_create < #{endTime}
order by report.gmt_create desc
limit #{current} , #{limit}
