mapper.select:
select userinfo.id as userId, userinfo.avatar , userinfo.nickname , userinfo.sign , attention.gmt_create
from user_attention as attention join user_info as userinfo on attention.attention_user_id = userinfo.id
where attention.user_id = #{userId}
order by attention.gmt_create desc
limit #{current} , #{limit}

